import React, { useEffect } from 'react';
import { customPageItem } from '../../types/customPageTypes';
import { useLocation } from 'react-router-dom';

interface ListSectionsProps {
  array: customPageItem[];
  activeValue: number;
  setActiveValue: (value: number) => void;
}

const ListSections = ({ array, activeValue, setActiveValue }: ListSectionsProps) => {
  const { hash } = useLocation();

  function getElementY(query: any) {
    return window.pageYOffset + document.querySelector(query).getBoundingClientRect().top;
  }

  function doScrolling(element: any, duration: any) {
    var startingY = window.pageYOffset;
    var elementY = getElementY(element);
    // If element is close to page's bottom then window will scroll only to some position above the element.
    var targetY =
      document.body.scrollHeight - elementY < window.innerHeight
        ? document.body.scrollHeight - window.innerHeight
        : elementY;
    var diff = targetY - startingY;
    // Easing function: easeInOutCubic
    // From: https://gist.github.com/gre/1650294
    var easing = function (t: any) {
      return t < 0.5 ? 4 * t * t * t : (t - 1) * (2 * t - 2) * (2 * t - 2) + 1;
    };
    let start: any;

    if (!diff) return;

    // Bootstrap our animation - it will get called right before next frame shall be rendered.
    window.requestAnimationFrame(function step(timestamp) {
      if (!start) start = timestamp;
      // Elapsed miliseconds since start of scrolling.
      var time = timestamp - start;
      // Get percent of completion in range [0, 1].
      var percent = Math.min(time / duration, 1);
      // Apply the easing.
      // It can cause bad-looking slow frames in browser performance tool, so be careful.
      percent = easing(percent);

      window.scrollTo(0, startingY + diff * percent);

      // Proceed with animation as long as we wanted it to.
      if (time < duration) {
        window.requestAnimationFrame(step);
      }
    });
  }
  useEffect(() => {}, []);
  return (
    <div className="boxListSections">
      {array.map(item => (
        <a
          key={item.id}
          className={`sectionListSection ${activeValue === item.id ? 'sectionListSectionActive' : ''}`}
          href={`#${item.id}`}
          onClick={event => {
            if (hash !== '') {
              // Prevent default anchor click behavior
              event.preventDefault();
              const element = document.getElementById(hash.replace('#', ''));
              if (element != null) {
                element.scrollIntoView();
              }
              // Using document.querySelector's animate() method to add smooth page scroll
              // The optional number (800) specifies the number of milliseconds it takes to scroll to the specified area
              //@ts-ignore
              // console.log('document.getElementById(hash)', document.getElementById(hash)?.offset());

              console.log('hash', hash);
              //@ts-ignore
              // document.querySelector('html, body').animate(
              //   {
              //     //@ts-ignore
              //     scrollTop: document.getElementById(hash)?.offsetTop(),
              //     //@ts-ignore
              //   },
              //   800,
              //   //@ts-ignore
              //   function () {
              //     // Add hash (#) to URL when done scrolling (default click behavior)
              //     window.location.hash = hash;
              //   },
              // );
            }
          }}
          // id={'menu' + String(item.id)}
        >
          {item.title}
        </a>
      ))}
    </div>
  );
};

export default ListSections;
