import React, { useEffect } from 'react';
import { customPageItem } from '../../types/customPageTypes';
import { useLocation } from 'react-router-dom';

interface ListSectionsProps {
  array: customPageItem[];
  activeValue: number;
  setActiveValue: (value: number) => void;
  setIsScroll: (value: boolean) => void;
  scrollValue: number;
  isScroll: boolean;
}

const ListSections = ({
  array,
  activeValue,
  setActiveValue,
  setIsScroll,
  isScroll,
  scrollValue,
}: ListSectionsProps) => {
  const { hash } = useLocation();

  const returnValue = () => {
    console.log('isScroll ? activeValue : scrollValue', isScroll ? activeValue : scrollValue);
    return isScroll ? activeValue : scrollValue;
  };
  return (
    <div className="boxListSections">
      {array.map(item => (
        <a
          key={item.id}
          className={`sectionListSection ${returnValue() == item.id ? 'sectionListSectionActive' : ''}`}
          href={`#${item.id}`}
          onClick={event => {
            setIsScroll(true);
            setActiveValue(item.id);
          }}
        >
          {item.title}
        </a>
      ))}
    </div>
  );
};

export default ListSections;
