import React, { useState, useEffect } from "react";

import { useLocation, useNavigate } from "react-router-dom";
import { authApi } from "../store";
import { UserData } from "../types/authTypes";
import Input from "../components/UI/Input";
import { Formik } from "formik";
import "../assets/scss/pages/_login.scss";

const Login = () => {
  const [logIn, { data: dataLogIn = null, error: errorLogIn }] =
    authApi.useLazySteamLoginQuery();
  const location = useLocation();
  const navigate = useNavigate();
  const [token, setToken] = useState<undefined | string>(undefined);
  const query = new URLSearchParams(location.search);

  //   useEffect(() => {
  //     const accessToken = localStorage.getItem("accessToken");
  //     if (accessToken === null) {
  //       const openidparams = query.get("openid.identity");

  //       if (openidparams) {
  //         const openId = openidparams.replace(
  //           "https://steamcommunity.com/openid/id/",
  //           ""
  //         );
  //         if (openId !== undefined) {
  //           logIn({ id: openId, body: query.toString() }).catch(() =>
  //             navigate("/")
  //           );
  //         }
  //       } else {
  //         navigate("/");
  //       }
  //     } else {
  //       setToken(accessToken);
  //     }
  //   }, []);

  useEffect(() => {
    if (errorLogIn) {
      navigate("/");
    } else {
      if (dataLogIn !== null) {
        const token: string | undefined = dataLogIn?.accessToken;
        const user: UserData | undefined = dataLogIn?.user;
        const refreshToken: string | undefined = dataLogIn?.refreshToken;
        if (token !== undefined && refreshToken != undefined) {
          localStorage.setItem("accessToken", token);
          localStorage.setItem("user", JSON.stringify(user));
          window.location.reload();
        } else {
          navigate("/");
        }
      }
    }
  }, [dataLogIn, errorLogIn]);

  return (
    <div className="box-login-page">
      <h1>ВОЙТИ</h1>
      <Formik
        initialValues={{ email: "", password: "" }}
        validate={(values) => {
          const errors = {};
          if (!values.email) {
            //@ts-ignore
            errors.email = "Required";
          } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
          ) {
            //@ts-ignore
            errors.email = "Invalid email address";
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
          }, 400);
        }}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
          /* and other goodies */
        }) => (
          <form onSubmit={handleSubmit} className="box-form-login">
            <input
              type="email"
              name="email"
              className="input"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.email}
            />
            {errors.email && touched.email && errors.email}
            <input
              type="password"
              name="password"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.password}
            />
            {errors.password && touched.password && errors.password}
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </form>
        )}
      </Formik>
    </div>
  );
};

export default Login;
