import React from "react";
import Modal from "../UI/Modal";
import { topUpMonitoringServers } from "../../consts/modal";
import { Formik, Field, useFormik, FormikProvider } from "formik";

interface MonitoringModalProps {
  isActive: boolean;
  setIsActive: (param: boolean) => void;
}

const CreateCaseModal = ({ isActive, setIsActive }: MonitoringModalProps) => {
  const formikProps = useFormik({
    initialValues: { email: "", password: "" },
    validate: (values) => {
      const errors = {};
      if (!values.email || !values.password) {
        //@ts-ignore
        errors.email = "Заполните все поля";
      } else if (
        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
      ) {
        //@ts-ignore
        errors.email = "Некорректный адрес электронной почты";
      }
      if (values.password.length < 8) {
        //@ts-ignore
        errors.password = "Некорректный пароль";
      }
      return errors;
    },
    onSubmit: (values, { setSubmitting }) => {},
  });

  return (
    <Modal
      isActive={isActive}
      setIsActive={setIsActive}
      headerTitle={"Cоздание заказа"}
      className="monitoringModal"
    >
      <>
        <div className="monitoringModalContainer">
          <FormikProvider value={formikProps}>
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting,
              /* and other goodies */
            }) => (
              <form
                onSubmit={handleSubmit}
                className="box-form-create-case-modal"
              >
                <div>
                  <p>НАЗВАНИЕ</p>
                  <input
                    type="string"
                    name="email"
                    className="input"
                    onChange={handleChange}
                    // onBlur={handleBlur}

                    value={values.email}
                  />
                </div>

                <div>
                  <p>ОПИСАНИЕ</p>
                  <Field
                    component="textarea"
                    rows="4"
                    onChange={(value) =>
                      values.setFieldValue("password", value)
                    }
                    value={values.password}
                  ></Field>
                </div>

                <div className="box-sign-up" onClick={() => {}}>
                  <p>{"ЗАРЕГИСТРИРОВАТЬСЯ"}</p>
                </div>
                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="lightBtn btn"
                >
                  {"ЗАРЕГИСТРИРОВАТЬСЯ"}
                </button>
                {(errors.email && touched.email && errors.email) ||
                  (errors.password && touched.password && errors.password)}
              </form>
            )}
          </FormikProvider>
        </div>
      </>
    </Modal>
  );
};

export default CreateCaseModal;
