import React, { useEffect, useState } from 'react';
import { customPageItem } from '../../types/customPageTypes';

interface ListSectionsProps {
  array: customPageItem[];
  activeValue: number;
  setActiveValue: (value: number) => void;
  setActiveScrollMenu: (value: boolean) => void;
  activeScrollMenu: boolean;
}

const ListSections = ({
  array,
  activeValue,
  setActiveValue,
  setActiveScrollMenu,
  activeScrollMenu,
}: ListSectionsProps) => {
  useEffect(() => {}, []);
  const [activeClickMenu, setActiveClickMenu] = useState(0);
  return (
    <div className="boxListSections">
      {array.map(item => (
        <a
          key={item.id}
          className={`sectionListSection ${
            (activeClickMenu !== 0 && activeClickMenu === item.id) || activeValue === item.id
              ? 'sectionListSectionActive'
              : ''
          }`}
          // href={`#${item.id}`}
          onClick={event => {
            // console.log(' setActiveScrollMenu true');
            // setActiveScrollMenu(true);
            setActiveClickMenu(item.id);

            const sections = document.getElementsByClassName('sectionCustomPage');
            let array = Array.from(sections);
            array.map((it: any, index: number) => {
              if (index + 1 === item.id) {
                it.scrollIntoView({
                  behavior: 'smooth',
                  block: 'start',
                }).then(() => {
                  setActiveClickMenu(0);
                });
              }
            });
            // setActiveClickMenu(0);
          }}
          // id={'menu' + String(item.id)}
        >
          {item.title}
        </a>
      ))}
    </div>
  );
};

export default ListSections;
