import "../../assets/scss/components/main/_case.scss";
import { ReactComponent as Trash } from "../../assets/img/trash.svg";
import { casesApi } from "../../store";

interface MenuProps {
  activeSection: number[];
  setActiveSection: (value: number[]) => void;
  openSection: number | null;
  setOpenSection: (value: number | null) => void;
}
const Menu = ({
  activeSection,
  setActiveSection,
  openSection,
  setOpenSection,
}: MenuProps) => {
  const sectionList = ["РАЗРАБОТКА", "ДИЗАЙН"];

  const subSection = ["FRONTEND", "BACKEND", "IOS", "ANDROID"];
  const handlerClickSection = (index: number) => {
    setActiveSection(
      activeSection.includes(index)
        ? activeSection.filter((item) => item !== index)
        : [...activeSection, index]
    );
  };

  const handlerOpenSubSection = (index: number) => {
    setOpenSection(openSection !== null ? null : index);
  };

  return (
    <div className="list-filter">
      <div className="header-box-filter">
        <h1>CФЕРЫ</h1>
        {activeSection.length !== 0 && (
          <p
            onClick={() => {
              setActiveSection([]);
            }}
          >
            <Trash />
          </p>
        )}
      </div>

      {sectionList.map((section, index: number) => {
        return (
          <div>
            <p
              key={index}
              className={`${
                activeSection.includes(index)
                  ? "active-section-list-filter"
                  : "section-list-filter"
              }`}
            >
              <div
                onClick={() => handlerClickSection(index)}
                className={`${
                  activeSection.includes(index) ? "active-flag-section" : ""
                } flag-section`}
              ></div>
              <div onClick={() => handlerOpenSubSection(index)}>{section}</div>
            </p>
            <div className="list-subsection">
              {openSection != null &&
                subSection.map((it) => (
                  <p
                    onClick={() => handlerClickSection(index)}
                    key={index}
                    className={`${
                      activeSection.includes(index)
                        ? "active-section-list-filter"
                        : "section-list-filter"
                    }`}
                  >
                    <div
                      className={`${
                        activeSection.includes(index)
                          ? "active-flag-section"
                          : ""
                      } flag-section`}
                    ></div>
                    <div>{it}</div>
                  </p>
                ))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Menu;
