import React, { useState, useRef, useEffect } from 'react';
import ListSections from './ListSections';
import { customPageApi } from '../../store';
import Loader from '../Loader';

interface CustomPageProps {
  id: number;
  haveSection: boolean;
}

const CustomPage = ({ haveSection, id }: CustomPageProps) => {
  const [activeValue, setActiveValue] = useState<number>(1);
  const [isScroll, setIsScroll] = useState(false);
  const [scrollValue, setScrollValue] = useState(1);
  const [lastScrollValue, setLastScrollValue] = useState(1);
  const [direction, setDirection] = useState('bottom');
  const { data: info } = customPageApi.useGetCustomPageQuery(id);
  let lastScrollTop = 0;
  useEffect(() => {
    const sections = document.getElementsByClassName('sectionCustomPage');
    window.addEventListener('scroll', () => {
      var st = window.pageYOffset || document.documentElement.scrollTop; // Credits: "https://github.com/qeremy/so/blob/master/so.dom.js#L426"
      if (st > lastScrollTop) {
        // console.log("setDirection('bottom');");
        setDirection('bottom');
      } else if (st < lastScrollTop) {
        // console.log("setDirection('top');");
        // setDirection('top');
      }
      lastScrollTop = st <= 0 ? 0 : st;

      let current = '';
      if (sections != null) {
        let array = Array.from(sections);
        array.map(section => {
          //@ts-ignore
          const sectionTop = section.offsetTop;
          const sectionHeight = section.clientHeight;

          if (window.pageYOffset >= sectionTop - sectionHeight / 3) {
            //@ts-ignore
            current = section.getAttribute('id');
          }
        });
      }
      setScrollValue(Number(current));
    });
  }, []);

  useEffect(() => {
    if (scrollValue === activeValue) {
      setIsScroll(false);
    }
  }, [scrollValue, activeValue]);

  // useEffect(() => {
  //   if (activeValue > scrollValue) {
  //     setDirection('bottom');
  //   } else {
  //     setDirection('top');
  //   }
  // }, [activeValue]);

  useEffect(() => {
    console.log('direction', direction);
  }, [direction]);

  useEffect(() => {
    if (direction === 'bottom' && lastScrollValue > scrollValue) {
      setIsScroll(false);
    } else if (direction === 'top' && lastScrollValue < scrollValue) {
      setIsScroll(false);
    }
    setLastScrollValue(scrollValue);
  }, [scrollValue]);

  // useEffect(() => {
  //   const navLi = document.getElementsByClassName('sectionListSection');
  //   if (navLi != null) {
  //     let array = Array.from(navLi);
  //     let scrollLeftValue = 0;
  //     array.map((li: any, index: any) => {
  //       // const i = li.getAttribute('ke');
  //       if (index + 1 === activeValue) {
  //         const navItems = document.getElementsByClassName('boxListSections');
  //         navItems[0].scrollLeft = scrollLeftValue;
  //       } else {
  //         scrollLeftValue += li.offsetWidth;
  //       }
  //     });
  //   }
  // }, [activeValue]);

  useEffect(() => {
    info &&
      info.sections.map(item => {
        let extractscript = /<script>(.+)<\/script>/gi.exec(item.html);
        if (extractscript != null) {
          window.eval(extractscript[1]);
        }
      });
  });

  if (!info) return <Loader />;
  return (
    <div className="containerCustomPage">
      <h1 className="titlePage">{info.text}</h1>
      <div className={` ${haveSection ? 'boxWithSection' : 'boxNoSection'}`}>
        {haveSection && (
          <ListSections
            array={info.sections}
            activeValue={activeValue}
            setActiveValue={setActiveValue}
            setIsScroll={setIsScroll}
            isScroll={isScroll}
            scrollValue={scrollValue}
          />
        )}
        <div>
          {info &&
            info.sections.map((item: any) => (
              <section key={item.id} className="sectionCustomPage" id={String(item.id)}>
                <div className={`${item.icon === null ? 'labelSectionCustomPage' : 'labelSectionCustomPageWithIcon'}`}>
                  <div className="iconSectionCustomPage">
                    {item.icon !== null && <img src={item.icon} className="iconCustomPage" />}
                  </div>
                  <h2>
                    {' '}
                    <a>{item.title}</a>
                  </h2>
                </div>
                {}
                <div className="question-text" dangerouslySetInnerHTML={{ __html: item.html }} />
              </section>
            ))}
        </div>
      </div>
    </div>
  );
};

export default CustomPage;
