import React, { useState, useEffect } from "react";
import { ReactComponent as Trash } from "../assets/img/trash.svg";
import SearchInput from "../components/UI/SearchInput";

const Main = () => {
  const [activeSection, setActiveSection] = useState<number[]>([]);
  const [openSection, setOpenSection] = useState<number | null>(null);

  const sectionList = ["РАЗРАБОТКА", "ДИЗАЙН"];

  const sortList = [
    "ПО ДАТЕ СОЗДАНИЯ",
    "ПО РЕЙТИНГУ ЗАКАЗЧИКА",
    "ПО КОЛИЧЕСТВУ ОТКЛИКОВ",
    "ПО РАЗМЕРУ СТОИМОСТИ",
  ];

  const subSection = ["FRONTEND", "BACKEND", "IOS", "ANDROID"];

  const handlerClickSection = (index: number) => {
    setActiveSection(
      activeSection.includes(index)
        ? activeSection.filter((item) => item !== index)
        : [...activeSection, index]
    );
  };

  const handlerOpenSubSection = (index: number) => {
    setOpenSection(openSection != null ? null : index);
  };

  const [value, setValue] = useState("");

  return (
    <div className="box-main-page container">
      <div>
        <SearchInput
          placeholder="Найти заказ"
          value={value}
          setValue={setValue}
        />
        <div className="box-list-sort">
          {sortList.map((sort, index) => {
            return (
              <div className="box-item-sort">
                <p>{sort}</p>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Main;
