    },
    {
      value: "sv",
      label: "СВ",
    },
  ];

  const select_family_status = [
    {
      value: "married",
      label: "Женат/замужем",
    },
    {
      value: "relationship",
      label: "В отношениях",
    },
    {
      value: "single",
      label: "Свободный",
    },
  ];

  const select_hobbies = [
    {
      value: "dance",
      label: "танцы",
    },
    {
      value: "single",
      label: "вокал",
    },
    {
      value: "paint",
      label: "рисование",
    },
  ];

  const select_place_position = [
    {
      value: "up",
      label: "Вверхняя полка",
    },
    {
      value: "down",
      label: "Нижняя полка",
    },
  ];

  const select_psy = [
    {
      value: "extrovert",
      label: "Экстроверт",
    },
    {
      value: "introvert",
      label: "Интроверт",
    },
    {
      value: "average",
      label: "Cредний",
    },
  ];

  const select_relig = [
    {
      value: "christianity",
      label: "Христианин",
    },
    {
      value: "islam",
      label: "Ислам",
    },
    {
      value: "judaism",
      label: "Иудаизм",
    },
    {
      value: "buddhism",
      label: "Буддизм",
    },
    {
      value: "judaism",
      label: "Иудаизм",
    },
    {
      value: "hinduism",
      label: "Индуизм",
    },
    {
      value: "sikhism",
      label: "Сикхизм",
    },
    {
      value: "hinduism",
      label: "Индуизм",
    },
    {
      value: "confucianism",
      label: "Конфуцианство",
    },
    {
      value: "taoism",
      label: "Даосизм",
    },
    {
      value: "shintoism",
      label: "Синтоизм",
    },
  ];

  const select_sex = [
    {
      value: "male",
      label: "Мужской",
    },
    {
      value: "female",
      label: "Женский",
    },
