import React, { useState, useRef, useEffect } from 'react';
import ListSections from './ListSections';
import { customPageApi } from '../../store';
import Loader from '../Loader';

interface CustomPageProps {
  id: number;
  haveSection: boolean;
}

const CustomPage = ({ haveSection, id }: CustomPageProps) => {
  const [activeValue, setActiveValue] = useState<number>(1);
  const { data: info } = customPageApi.useGetCustomPageQuery(id);

  useEffect(() => {
    const headings = document.querySelectorAll('div[id]');
    document.addEventListener('scroll', e => {
      headings.forEach(ha => {
        const rect = ha.getBoundingClientRect();
        if (rect.top > 0 && rect.top < 300) {
          const location = window.location.toString().split('#')[0];
          history.replaceState(null, '', location + '#' + ha.id);
          setActiveValue(Number(ha.id));
        }
      });
    });
  }, []);
  const sections = document.querySelectorAll('section');
  const navLi = document.querySelectorAll('nav .container ul li');
  window.addEventListener('scroll', () => {
    let current = '';
    sections.forEach(section => {
      const sectionTop = section.offsetTop;
      const sectionHeight = section.clientHeight;
      if (pageYOffset >= sectionTop - sectionHeight / 3) {
        current = section.getAttribute('id');
      }
    });

    navLi.forEach(li => {
      li.classList.remove('active');
      if (li.classList.contains(current)) {
        li.classList.add('active');
      }
    });
  });

  // *********************
  // This Code is for only the floating card in right bottom corner
  // **********************

  const WebCifarIcon = document.querySelector('#webCifar-icon');
  const WebCifarEl = document.querySelector('#webCifar');
  const close = WebCifarEl.querySelector('.close');
  const youtubeLink = document.querySelector('.youtubeLink');

  WebCifarIcon.addEventListener('click', () => {
    WebCifarEl.classList.add('active');
  });
  close.addEventListener('click', () => {
    WebCifarEl.classList.remove('active');
  });

  youtubeLink.setAttribute('href', 'https://youtu.be/RsPWEmfOQdU');

  useEffect(() => {
    info &&
      info.sections.map(item => {
        let extractscript = /<script>(.+)<\/script>/gi.exec(item.html);
        if (extractscript != null) {
          window.eval(extractscript[1]);
        }
      });
  });

  if (!info) return <Loader />;
  return (
    <div className="containerCustomPage">
      <h1 className="titlePage">{info.text}</h1>
      <div className={` ${haveSection ? 'boxWithSection' : 'boxNoSection'}`}>
        {haveSection && (
          <ListSections array={info.sections} activeValue={activeValue} setActiveValue={setActiveValue} />
        )}
        <div>
          {info &&
            info.sections.map((item: any) => (
              <div key={item.id} className="sectionCustomPage">
                <div
                  className={`${item.icon === null ? 'labelSectionCustomPage' : 'labelSectionCustomPageWithIcon'}`}
                  id={String(item.id)}
                >
                  <div className="iconSectionCustomPage">
                    {item.icon !== null && <img src={item.icon} className="iconCustomPage" />}
                  </div>
                  <h2>
                    {' '}
                    <a>{item.title}</a>
                  </h2>
                </div>
                {}
                <div className="question-text" dangerouslySetInnerHTML={{ __html: item.html }} />
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default CustomPage;
