import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import { baseQuery } from "./settings";
import { steamLoginData } from "../types/authTypes";

export const authApi = createApi({
  reducerPath: "authApi",
  baseQuery,
  endpoints: (build) => ({
    logIn: build.query<steamLoginData, { id: string; body: any }>({
      query: ({ id, body }) => ({
        url: `/auth/signup`,
        method: "POST",
        credentials: "include",
        headers: {
          signature: body,
        },
      }),
    }),
    logOut: build.query<void, void>({
      query: () => ({
        url: "/auth/logout",
        method: "GET",
      }),
    }),
  }),
});
