import React, { useEffect } from 'react';
import { customPageItem } from '../../types/customPageTypes';
import { useLocation } from 'react-router-dom';

interface ListSectionsProps {
  array: customPageItem[];
  activeValue: number;
  setActiveValue: (value: number) => void;
}

const ListSections = ({ array, activeValue, setActiveValue }: ListSectionsProps) => {
  const { hash } = useLocation();
  useEffect(() => {}, []);
  return (
    <div className="boxListSections">
      {array.map(item => (
        <a
          key={item.id}
          className={`sectionListSection ${activeValue === item.id ? 'sectionListSectionActive' : ''}`}
          href={`#${item.id}`}
          onClick={event => {
            if (hash !== '') {
              // Prevent default anchor click behavior
              event.preventDefault();

              // Using document.querySelector's animate() method to add smooth page scroll
              // The optional number (800) specifies the number of milliseconds it takes to scroll to the specified area
              //@ts-ignore
              document.querySelector('html, body').animate(
                {
                  //@ts-ignore
                  scrollTop: document.querySelector(hash).offset().top,
                  //@ts-ignore
                },
                800,
                function () {
                  // Add hash (#) to URL when done scrolling (default click behavior)
                  window.location.hash = hash;
                },
              );
            }
          }}
          // id={'menu' + String(item.id)}
        >
          {item.title}
        </a>
      ))}
    </div>
  );
};

export default ListSections;
