import React, { useState, useEffect } from "react";
import { ReactComponent as Trash } from "../assets/img/trash.svg";
import SearchInput from "../components/UI/SearchInput";

const Main = () => {
  const [activeSection, setActiveSection] = useState<number[]>([]);
  const [openSection, setOpenSection] = useState<number | null>(null);

  const sectionList = ["РАЗРАБОТКА", "ДИЗАЙН"];

  const sortList = [
    "ПО ДАТЕ СОЗДАНИЯ",
    "ПО РЕЙТИНГУ ЗАКАЗЧИКА",
    "ПО КОЛИЧЕСТВУ ОТКЛИКОВ",
    "ПО РАЗМЕРУ СТОИМОСТИ",
  ];

  const subSection = ["FRONTEND", "BACKEND", "IOS", "ANDROID"];

  const handlerClickSection = (index: number) => {
    if (activeSection.includes(index)) {
      setActiveSection(activeSection.filter((item) => item !== index));
    } else {
      setActiveSection([...activeSection, index]);
    }
  };

  const [value, setValue] = useState("");

  return (
    <div className="box-main-page container">
      <div>
        <SearchInput
          placeholder="Найти заказ"
          value={value}
          setValue={setValue}
        />
        <div className="box-sort"></div>
      </div>
      <div className="list-filter">
        <div className="header-box-filter">
          <h1>CФЕРЫ</h1>
          {activeSection.length !== 0 && (
            <p
              onClick={() => {
                setActiveSection([]);
              }}
            >
              <Trash />
            </p>
          )}
        </div>

        {sectionList.map((section, index) => {
          return (
            <div>
              <p
                key={index}
                className={`${
                  activeSection.includes(index)
                    ? "active-section-list-filter"
                    : "section-list-filter"
                }`}
              >
                <div
                  onClick={() => handlerClickSection(index)}
                  className={`${
                    activeSection.includes(index) ? "active-flag-section" : ""
                  } flag-section`}
                ></div>
                <div onClick={() => setOpenSection(index)}>{section}</div>
              </p>
              <div className="list-subsection">
                {openSection != null &&
                  subSection.map((it) => (
                    <p
                      onClick={() => handlerClickSection(index)}
                      key={index}
                      className={`${
                        activeSection.includes(index)
                          ? "active-section-list-filter"
                          : "section-list-filter"
                      }`}
                    >
                      <div
                        className={`${
                          activeSection.includes(index)
                            ? "active-flag-section"
                            : ""
                        } flag-section`}
                      ></div>
                      <div>{it}</div>
                    </p>
                  ))}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Main;
