import React, { useEffect, useState } from 'react';
import { customPageItem } from '../../types/customPageTypes';

interface ListSectionsProps {
  array: customPageItem[];
  activeValue: number;
  setActiveValue: (value: number) => void;
  setActiveScrollMenu: (value: boolean) => void;
  activeScrollMenu: boolean;
  activeClickMenu: number;
  setActiveClickMenu: (value: number) => void;
}

const ListSections = ({
  array,
  activeValue,
  setActiveValue,
  setActiveScrollMenu,
  activeScrollMenu,
  activeClickMenu,
  setActiveClickMenu,
}: ListSectionsProps) => {
  function disableScroll() {
    // Get the current page scroll position in the vertical direction
    let scrollTop = window.pageYOffset || document.documentElement.scrollTop;

    // Get the current page scroll position in the horizontal direction

    let scrollLeft = window.pageXOffset || document.documentElement.scrollLeft;

    // if any scroll is attempted,
    // set this to the previous value
    window.onscroll = function () {
      window.scrollTo(scrollLeft, scrollTop);
    };
  }
  return (
    <div className="boxListSections">
      {array.map(item => (
        <a
          key={item.id}
          className={`sectionListSection ${activeValue === item.id ? 'sectionListSectionActive' : ''}`}
          href={`#${item.id}`}
          onClick={event => {
            // console.log(' setActiveScrollMenu true');
            // setActiveScrollMenu(true);

            // setActiveClickMenu(item.id);

            const sections = document.getElementsByClassName('sectionCustomPage');
            let array = Array.from(sections);
            array.map((it: any, index: number) => {
              if (index + 1 === item.id) {
                if (window.location.hash !== '') {
                  event.preventDefault();
                }
                it.scrollIntoView({
                  behavior: 'smooth',
                  block: 'start',
                });
              }
            });
            // disableScroll();
          }}
          // id={'menu' + String(item.id)}
        >
          {item.title}
        </a>
      ))}
    </div>
  );
};

export default ListSections;
