import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";

import { baseQuery } from "./settings";
import {
  getBalanceData,
  getDetailsData,
  getInventoryData,
  buyProductParams,
  getDetailsParams,
  getInventoryParams,
  Refill,
  RefillProps,
  getPriceProductParams,
  getPriceCurrencyData,
  getPriceCurrencyParams,
  IGetCode,
  activateServiceParams,
  Contact,
} from "../types/userTypes";

export const userApi = createApi({
  reducerPath: "userApi",
  tagTypes: ["user"],
  baseQuery,
  endpoints: (build) => ({
    getUser: build.query<{ user: user }, string>({
      query: (id) => ({
        url: `/user/${id}`,
        method: "GET",
      }),
      providesTags: () => ["user"],
    }),
    changePhoto: build.mutation<any, any>({
      query: (body) => ({
        url: `/user/logo`,
        method: "POST",
        body: body,
      }),

      invalidatesTags: () => ["user"],
    }),
  }),
});
