import ru from '../assets/img/footer/changeLanguage/ru.svg';

export const select_language = [
  {
    value: 'ru',
    label: 'RU',
    icon: ru,
  },
  {
    value: 'en',
    label: 'EN',
    icon: ru,
  },
];

export const footerInformafionPosted = {
  ru: 'Размещенная на настоящем сайте информация носит исключительно информационный характер\nи ни при какихусловиях не является публичной офертой, определяемой положениями ч. 2 ст. 437 ГК РФ',
  en: 'The information posted on this site is for informational purposes only and under no circumstances is a public offer, as defined by the provisions of Part 2, Art. 437 of the Civil Code of the Russian Federation',
};

export const footerConditionsOfUse = {
  ru: 'Условия использовавания',
  en: 'Conditions of use',
};
