import React, { useEffect } from 'react';
import { customPageItem } from '../../types/customPageTypes';

interface ListSectionsProps {
  array: customPageItem[];
  activeValue: number;
  setActiveValue: (value: number) => void;
  setActiveScrollMenu: (value: boolean) => void;
  activeScrollMenu: boolean;
}

const ListSections = ({
  array,
  activeValue,
  setActiveValue,
  setActiveScrollMenu,
  activeScrollMenu,
}: ListSectionsProps) => {
  useEffect(() => {}, []);
  return (
    <div className="boxListSections">
      {array.map(item => (
        <a
          key={item.id}
          className={`sectionListSection ${activeValue === item.id ? 'sectionListSectionActive' : ''}`}
          // href={`#${item.id}`}
          onClick={() => {
            console.log(' setActiveScrollMenu true');
            setActiveScrollMenu(true);
            const sections = document.getElementsByClassName('sectionCustomPage');
            let array = Array.from(sections);
            function scrollElemIntoView(elem, options) {
              return new Promise((resolve, reject) => {
                if (!elem) {
                  reject('Cannot scroll as the target element does not exist');
                  return;
                }
                elem.scrollIntoView({
                  behavior: 'smooth',
                  onScrollEnd: resolve,
                  ...options,
                });
              });
            }

            array.map((it: any, index: number) => {
              if (index + 1 === item.id) {
                scrollElemIntoView(it, { block: 'start' }).then(scrollEvent => {
                  console.log('The browser has finished scrolling');
                });
              }
            });
          }}
          // id={'menu' + String(item.id)}
        >
          {item.title}
        </a>
      ))}
    </div>
  );
};

export default ListSections;
