import React, { useState, useEffect, useRef } from 'react';
import '../assets/scss/pages/_ban-list.scss';
import SearchInput from '../components/UI/SearchInput';
import Table from '../components/UI/Table';
import BanListTableItem from '../components/BanList/BanListTableItem';
import { rustApi } from '../store';
import Pagination from '../components/Pagination/Pagination';
import { header } from '../consts/banList';
import Loader from '../components/Loader';
import { useLang } from '../hooks/useLang';
import { NoRecords, banListPageInputPlaceholder, banListPageTitle } from '../consts/leadersAndBanlist';

const perPage = 10;

const BanList = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [value, setValue] = useState('');

  const { data, isLoading } = rustApi.useGetBanListQuery({ page: currentPage, count: perPage, searchValue: value });

  useEffect(() => {
    setCurrentPage(1);
  }, [value]);

  const tableRef = useRef<any | null | React.LegacyRef<HTMLDivElement>>();

  useEffect(() => {
    tableRef?.current?.scrollTo({ left: 0, behavior: 'smooth' });
  }, [currentPage]);

  if (isLoading) {
    return <Loader />;
  }

  return (
    <div className="container">
      <h2 className="titlePage">{useLang(banListPageTitle)}</h2>
      <SearchInput placeholder={useLang(banListPageInputPlaceholder)} value={value} setValue={setValue} />

      {!data || (data.banlist.length === 0 && <h1 className="noRecords">{useLang(NoRecords)}</h1>)}
      <div ref={tableRef} className="tableWrap">
        {!(!data || data.banlist.length === 0) && (
          <Table header={header} data={data.banlist} ItemComponent={BanListTableItem} className="tableForBanList" />
        )}
      </div>
      <Pagination
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
        pagesAmount={data ? data.pages : 1}
        perPage={perPage}
      />
    </div>
  );
};

export default BanList;
