import React, { useEffect } from 'react';
import { customPageItem } from '../../types/customPageTypes';

interface ListSectionsProps {
  array: customPageItem[];
  activeValue: number;
  setActiveValue: (value: number) => void;
  setIsScroll: (value: boolean) => void;
  scrollValue: number;
  isScroll: boolean;
}

const ListSections = ({
  array,
  activeValue,
  setActiveValue,
  setIsScroll,
  isScroll,
  scrollValue,
}: ListSectionsProps) => {
  const returnValue = () => {
    return isScroll ? activeValue : scrollValue;
  };

  // useEffect(() => {
  //   console.log('active', returnValue());
  // }, [isScroll, activeValue, scrollValue]);

  return (
    <div className="boxListSections">
      {array.map(item => (
        <a
          key={item.id}
          className={`sectionListSection ${returnValue() == item.id ? 'sectionListSectionActive' : ''}`}
          href={`#${item.id}`}
          onClick={event => {
            setIsScroll(true);
            setActiveValue(item.id);
          }}
        >
          {item.title}
        </a>
      ))}
    </div>
  );
};

export default ListSections;
