import React from 'react';
import { customPageItem } from '../../types/customPageTypes';

interface ListSectionsProps {
  array: customPageItem[];
  activeValue: number;
}

const ListSections = ({ array, activeValue }: ListSectionsProps) => {
  return (
    <div>
      <div className="boxListSections">
        {array.map(item => (
          <a
            key={item.id}
            className={`sectionListSection ${activeValue === item.id ? 'sectionListSectionActive' : ''}`}
            href={`#${item.id}`}
          >
            {item.title}
          </a>
        ))}
      </div>
    </div>
  );
};

export default ListSections;
