import React, { useState, useRef, useEffect } from 'react';
import ListSections from './ListSections';
import { customPageApi } from '../../store';
import Loader from '../Loader';

interface CustomPageProps {
  id: number;
  haveSection: boolean;
}

const CustomPage = ({ haveSection, id }: CustomPageProps) => {
  const [activeValue, setActiveValue] = useState<number>(1);
  const { data: info } = customPageApi.useGetCustomPageQuery(id);

  const scrollTo = (el: any) => {
    const elRight = el.offsetLeft + el.offsetWidth;
    const elLeft = el.offsetLeft;

    const elParentRight = el.parentNode.offsetLeft + el.parentNode.offsetWidth;
    const elParentLeft = el.parentNode.offsetLeft;

    // check if right side of the element is not in view
    if (elRight > elParentRight + el.parentNode.scrollLeft) {
      el.parentNode.scrollLeft = elRight - elParentRight;
    }

    // check if left side of the element is not in view
    else if (elLeft < elParentLeft + el.parentNode.scrollLeft) {
      el.parentNode.scrollLeft = elLeft - elParentLeft;
    }
  };

  useEffect(() => {
    const sections = document.getElementsByClassName('sectionCustomPage');
    const navLi = document.getElementsByClassName('sectionListSection');

    console.log('navLi', navLi);
    console.log('sections', sections);
    window.addEventListener('scroll', () => {
      let current = '';
      if (sections != null) {
        let array = Array.from(sections);
        array.map(section => {
          //@ts-ignore
          const sectionTop = section.offsetTop;
          const sectionHeight = section.clientHeight;

          if (window.pageYOffset >= sectionTop - sectionHeight / 3) {
            //@ts-ignore
            current = section.getAttribute('id');
          }
        });
      }
      setActiveValue(Number(current));
      if (navLi != null) {
        let array = Array.from(navLi);
        array.map((li: any, index: any) => {
          const i = li.getAttribute('id');
          console.log('i', i);
          if (i === 'menu' + current) {
            const navItems = document.getElementsByClassName('boxListSections');
            console.log('navItems', navItems);
            scrollTo(li);

            const navItemsRect = navItems[0].getBoundingClientRect();
            const navItemActiveRect = li.getBoundingClientRect();
            const navItemsLeft =
              navItemActiveRect.left - navItemsRect.left + (navItemActiveRect.width - navItemsRect.width) / 2;
            console.log('navItemsLeft', navItemsLeft);
            navItems[0].scrollLeft = navItemsLeft;
          }
        });
      }
    });
  }, []);

  useEffect(() => {
    info &&
      info.sections.map(item => {
        let extractscript = /<script>(.+)<\/script>/gi.exec(item.html);
        if (extractscript != null) {
          window.eval(extractscript[1]);
        }
      });
  });

  if (!info) return <Loader />;
  return (
    <div className="containerCustomPage">
      <h1 className="titlePage">{info.text}</h1>
      <div className={` ${haveSection ? 'boxWithSection' : 'boxNoSection'}`}>
        {haveSection && (
          <ListSections array={info.sections} activeValue={activeValue} setActiveValue={setActiveValue} />
        )}
        <div>
          {info &&
            info.sections.map((item: any) => (
              <section key={item.id} className="sectionCustomPage" id={String(item.id)}>
                <div className={`${item.icon === null ? 'labelSectionCustomPage' : 'labelSectionCustomPageWithIcon'}`}>
                  <div className="iconSectionCustomPage">
                    {item.icon !== null && <img src={item.icon} className="iconCustomPage" />}
                  </div>
                  <h2>
                    {' '}
                    <a>{item.title}</a>
                  </h2>
                </div>
                {}
                <div className="question-text" dangerouslySetInnerHTML={{ __html: item.html }} />
              </section>
            ))}
        </div>
      </div>
    </div>
  );
};

export default CustomPage;
