import React, { useState, useEffect } from "react";
import "../assets/scss/pages/_flight.scss";
import ScaleOnline from "../components/Footer/ScaleOnline";

import { flightApi } from "../store";
import Loader from "../components/Loader";
import ReservedWagon from "../components/Main/ReservedWagon";

const Flight = () => {
  const flightId = window.location.pathname.replace("/flight/", "");
  const { data: flight, isLoading } = flightApi.useGetFlightQuery(flightId);
  const [selectWagon, setSelectWagon] = useState<string>(
    (flight?.flights[0]?.flightPlaces &&
      Object.keys(flight?.flights[0]?.flightPlaces)[0]) ||
      "0"
  );
  const [isShowResponseModal, setIsShowResponseModal] = useState(false);
  const [isShowUpdateModal, setIsShowUpdateModal] = useState(false);
  const [isShowDeclineOrder, setIsShowDeclineOrder] = useState(false);
  const [isShowDone, setIsShowDone] = useState(false);

  useEffect(() => {
    if (flight?.flights[0] !== undefined) {
      setSelectWagon(Object.keys(flight?.flights[0]?.flightPlaces)[0]);
    }
  }, [flight]);

  const user =
    localStorage.getItem("user") != null
      ? //@ts-ignore
        JSON.parse(localStorage.getItem("user"))
      : undefined;

  if (isLoading || !flight?.flights[0]?.flightPlaces) return <Loader />;
  return (
    <div className="container box-deal-page ">
      {Object.keys(flight?.flights[0]?.flightPlaces).map((item, index) => {
        if (selectWagon === item) {
          return (
            <>
              <div className="title-box-wagon">
                <button
                  onClick={() => {
                    const keys = Object.keys(flight?.flights[0]?.flightPlaces);
                    let nextIndex = keys.indexOf(item) - 1;
                    let nextItem = keys[nextIndex];
                    setSelectWagon(nextItem);
                  }}
                >
                  ❮{" "}
                </button>
                <h2>Вагон: {item}</h2>
                <button
                  onClick={() => {
                    const keys = Object.keys(flight?.flights[0]?.flightPlaces);
                    let nextIndex = keys.indexOf(item) + 1;
                    let nextItem = keys[nextIndex];
                    if (nextIndex === undefined) {
                    } else {
                      setSelectWagon(nextItem);
                    }
                  }}
                >
                  {" "}
                  ❯
                </button>
              </div>

              <ReservedWagon />
            </>
          );
        }
      })}
    </div>
  );
};

export default Flight;
